import React, { useState } from "react";

function App() {
  const [jobDescription, setJobDescription] = useState("");
  const [resumeFile, setResumeFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!jobDescription || !resumeFile) {
      alert("Both resume and job description are required.");
      return;
    }

    const formData = new FormData();
    formData.append("job_description", jobDescription);
    formData.append("resume", resumeFile);

    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/enhance", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Upload failed");

      const blob = await res.blob();
      const downloadUrl = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = downloadUrl;
      link.download = "enhanced_resume.docx";
      link.click();
    } catch (err) {
      alert("Error uploading: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-white px-6 py-10 font-sans">
      {/* Header */}
      <header className="flex items-center justify-between mb-12">
        <div className="flex items-center gap-3">
          <img src="/logo.png" alt="logo" className="w-8 h-12" />
          <h1 className="text-2xl font-bold text-gray-800">AI Resume Enhance</h1>
        </div>
      </header>

      {/* Main content */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
        {/* Job Description */}
        <div>
          <h2 className="text-4xl font-bold mb-4">Tailor your resume to any job</h2>
          <p className="text-gray-600 mb-6">
            Paste a job description and upload your resume. Our AI will rewrite your skills section to match.
          </p>

          <textarea
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
            placeholder="Paste job description here..."
            className="w-full h-48 p-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500"
          ></textarea>

          <button
            onClick={handleSubmit}
            className="mt-4 bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-lg font-semibold"
            disabled={loading}
          >
            {loading ? "Enhancing..." : "Enhance Resume"}
          </button>
        </div>

        {/* Resume Upload + Preview */}
        <div className="flex flex-col items-center justify-center border border-dashed border-gray-400 p-6 rounded-lg">
          {!previewUrl ? (
            <>
              <input
                type="file"
                accept=".docx"
                onChange={(e) => {
                  const file = e.target.files[0];
                  if (file) {
                    setResumeFile(file);
                    setPreviewUrl(URL.createObjectURL(file));
                  }
                }}
                className="mb-4"
              />
              <p className="text-gray-500 text-center">Upload your resume (.docx)</p>
            </>
          ) : (
            <iframe
              src={previewUrl}
              title="Resume Preview"
              className="w-full h-64 border rounded-md"
            />
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
